import React, { useState, useEffect } from 'react';
import Axios  from 'axios';
import './new.css';
import NewMovies from '../components/newmovies';
import { GrNext } from 'react-icons/gr';
import { GrPrevious } from 'react-icons/gr';
//https://api.themoviedb.org/3/movie/latest?api_key=<<api_key>>&language=en-US
//FcNext
//FcPrevious
function New() {

    const slideLeft = () => {

        let slider = document.getElementById('slider');
        slider.scrollLeft -= 50;
        console.log(slider);
      };

    const slideRight = () => {

        let slider = document.getElementById('slider');
        slider.scrollLeft += 50;
        console.log(slider);
    };

    const sideScroll = (
        element: HTMLDivElement,
        speed: number,
        distance: number,
        step: number
      ) => {
        let scrollAmount = 0;
        const slideTimer = setInterval(() => {
          element.scrollLeft += step;
          scrollAmount += Math.abs(step);
          if (scrollAmount >= distance) {
            clearInterval(slideTimer);
          }
        }, speed);
      };

    return (
        <div className="new-movies-container">
            <div className="heading">
                <p>New Releases</p>
            </div>
            <div className = "new-movies-slider" id="slider">
                <div className="leftIcon"><GrPrevious onClick={ sideScroll() }/></div>
                <div className="rightIcon"><GrNext  onClick={ slideRight }/></div>

                <NewMovies></NewMovies>
            </div>

        </div>
    )
}

export default New;
