import React, { useState, useEffect } from 'react';
import Axios  from 'axios';
import 'new.css'
//https://api.themoviedb.org/3/movie/latest?api_key=<<api_key>>&language=en-US

function NewMovies() {

    const url = 'https://api.themoviedb.org/3/movie/now_playing?api_key=29c6212e49ed62a5bd8f2c72cd239af9&language=en-US&page=1';
    const posterUrl = 'https://image.tmdb.org/t/p/original';
    //const imageUrl = posterUrl.concat(props.image);
    const [newMovies, setNewMovies] = useState([]);

    useEffect(()=> {
        Axios.get(url)
        .then((res) => {
            //console.log(res.data.results);
            setNewMovies(res.data.results);
        })
    },[])


    console.log(newMovies);

    return (
        <div className="new-movies-container">
            <div className="heading">
                <p>New Releases</p>
            </div>

        {
             newMovies.map((movie) => {
                //console.log(movie.title);
                 return (
                   <div className="movie-list">
                       <div className="movie-image"><img src = { posterUrl.concat(movie.poster_path) } width="200px" height="auto" alt= {movie.title} /></div>
                       <div className="movie-title"> { movie.title }</div>
                        <div className="movie-releaseDate">{ movie.release_date } </div>

                    </div>

                 )



            })
        }


        </div>
    )
}

export default NewMovies;
