import './movieModal.css';


function MovieModal( props ) {

  const close = () => {
      props.hideModal(false);
      console.log('hey');
  }

  return (
    <div className="movie-modal">

        <div id="my-modal" className="modal">

        <div className="modal-content">
            <div className="modal-close" ><span className="close" >&times;</span></div>

            <div className="movie-image" onClick={ close }>
                <img src = { props.image } width="200px" height="auto" alt= {props.title} />
            </div>
            <div className="movie-title">{ props.title }</div>
            <div className="movie-releasedate"><b>Release date:</b> { props.releaseDate }</div>
            <div className="movie-overview">Overview: { props.overview }</div>
        </div>

        </div>
    </div>
  );
}

export default MovieModal;
