import React, { useRef } from 'react';
import Hero from '../components/hero';
import NewMovies from '../components/newmovies';
import '../index.css'
import Popular from './popular';
import  { useState, useEffect } from 'react';
import Axios from 'axios';
import { GrNext } from 'react-icons/gr';
import { GrPrevious } from 'react-icons/gr';
import PopularMovies from '../components/popularMovies';


function MainPage() {

    const [newMovies, setNewMovies] = useState([]);

    const url = 'https://api.themoviedb.org/3/movie/now_playing?api_key=29c6212e49ed62a5bd8f2c72cd239af9&language=en-US';



    const containerRef = useRef(null);

    const slide = (shift) => {
        containerRef.scrollLeft += shift;
        console.log('here we go');
      };


      useEffect(()=> {
        Axios.get(url)
        .then((res) => {
            setNewMovies(res.data.results);
        })
    },[]);

    return (
        <div>
            <Hero />
            <div class="new-movies-homepage">
                <div className="heading">
                    <p>New Movies</p>
                </div>
                <div class="leftIcon" onClick= { slide(-50) }><GrPrevious /></div>
                <div class="rightIcon" onClick= { slide(50) }><GrNext  /></div>
                 <NewMovies></NewMovies>
            </div>

            <div className="popular-movies-homepage">
                <div className="heading">
                    <p>Popular Movies</p>
                </div>
                <div class="leftIcon"><GrPrevious /></div>
                <div class="rightIcon"><GrNext  /></div>
                <PopularMovies></PopularMovies>
            </div>
        </div>
    )
}

export default MainPage;
