import React, { useState, useEffect } from 'react';
import Axios  from 'axios';
import './new.css';
import NewMovies from '../components/newmovies';
import { GrNext } from 'react-icons/gr';
import { GrPrevious } from 'react-icons/gr';
//https://api.themoviedb.org/3/movie/latest?api_key=<<api_key>>&language=en-US

function New() {

    const slider = document.getElementById('slider');
    const containerRef = React.useRef();

    const slide = (shift) => {
        containerRef.scrollLeft += shift;
        console.log('here we go');
      };


    return (
        <div className="new-movies-container">
            <div className="heading">
                <p>New Releases</p>
            </div>
            <div className = "new-movies-slider" id="slider">
                <div className="leftIcon"><GrPrevious onClick={() => slide(-50)}/></div>
                <div className="rightIcon"><GrNext  onClick={() => slide(+50)}/></div>           
                <NewMovies></NewMovies>
            </div>            
        </div>
    )
}

export default New;
