import React, { useState } from 'react';
import Axios from 'axios';
import Movies from '../components/movies';
import InputForm from '../components/inputForm';


function Search() {

    const [data, setData] = useState([]);
    const [input, setInput] = useState('Liger');

    //const input = 'Liger';


    //input data will come from inputForm component
    const url = `https://api.themoviedb.org/3/search/movie/?api_key=29c6212e49ed62a5bd8f2c72cd239af9&query=${input}`;
    const posterUrl = 'https://image.tmdb.org/t/p/original';

    const submitHandler = (data) => {
            setData(data);


    }

    return (
        <div>

       <InputForm submitHandler = {submitHandler}/>
        <div className="movies-container" >

          {
            data.map((movie) => {
                return (

                  <Movies  
                    key = {movie.id}
                    movieID = { movie.id.toString() } 
                    adult = { movie.adult }
                    url = { posterUrl }
                    image = { movie.poster_path } 
                    title = { movie.title }
                    releaseDate = { movie.release_date }
                    overview = { movie.overview }
                    ></Movies>
                )


            })
          } 

         </div>
         </div>    
    )

}

export default Search;
