import React, { useState } from 'react';
import Axios  from 'axios';

//https://api.themoviedb.org/3/movie/latest?api_key=<<api_key>>&language=en-US

function NewMovies() {

    const url = 'https://api.themoviedb.org/3/movie/now_playing?api_key=29c6212e49ed62a5bd8f2c72cd239af9&language=en-US&page=1';
    const [newMovies, setNewMovies] = useState([]);

    Axios.get(url)
    .then((res) => {
        console.log(res.data.results);
        setNewMovies(res.results);
    })

    console.log(newMovies);

    return (
        <div className="new-movie-container">
            <div className="heading">
                <p>New Releases</p>
            </div>
            <div class="movies-container" >
        {
             newMovies.map((movie) => {
                <div className="movie-data-container">
                    <div className="movie-image">   


                        {/* <img src = { imageUrl } width="200px" height="auto" alt= {props.title} /> */}

                    </div>    
                    <div className="movie-title">  
                         <b> { movie.title } </b>        
                    </div>
                    <div className="movie-year">
                        <b>Release Date:</b> { movie.releaseDate }
                    </div>
                    <div className="movie-overview">
                        { movie.overview }

                    </div> 

                </div> 

            })
        }
        </div>

        </div>
    )
}

export default NewMovies;
